INSERT INTO `tk_document` VALUES ('16', '系统名称school hospital的配置', '<p>\r\n	文件：EndpointsAction.class.php\r\n</p>\r\n<p>\r\n	位置：\\Apps\\Lib\\Action\\Madmin\\\r\n</p>\r\n<p>\r\n	原来：$params = array(\'touchMainId\'=&gt;$tmid,\'commandName\'=&gt;$commandName,\'commandParam1\'=&gt;$commandParam1,\'commandParam2\'=&gt;\'\',\'commandNote\'=&gt;$commandNote);\r\n</p>\r\n<p>\r\n	修改：$params = array(\'systemName\'=&gt;C(\"SYSTEMNAME\"),\'touchMainId\'=&gt;$tmid,\'commandName\'=&gt;$commandName,\'commandParam1\'=&gt;$commandParam1,\'commandParam2\'=&gt;\'\',\'commandNote\'=&gt;$commandNote);\r\n</p>\r\n<p>\r\n	修改原因：董工要求增加，systemName\r\n</p>\r\n<p>\r\n	取值：school,hospital或其它\r\n</p>\r\n<p>\r\n	配置：\\Apps\\Conf\\Madmin\\config.php\r\n</p>\r\n<p>\r\n	配置示例：\r\n</p>\r\n<p>\r\n	\'SYSTEMNAME\'			=&gt;	\'hospital\',\r\n</p>\r\n<p>\r\n	或\r\n</p>\r\n<p>\r\n	\'SYSTEMNAME\'			=&gt;	\'school\',\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	配置读取方法：\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"></span><span style=\"line-height:1.5;\">C(\"SYSTEMNAME\")</span><span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	其它备忘：\r\n</p>\r\n<p>\r\n	终端管理中，选一个终端，然后发送插播消息，有误，后增加的这个参数。\r\n</p>\r\n<p>\r\n	现在阿里云的TE和CS都做了相同的改动，本机的暂时不加\r\n</p>', '', '-1', '-1', '', '13', '2016-08-22 09:33:32', '13', '2016-08-22 09:53:21', '0', '');
INSERT INTO `tk_document` VALUES ('17', 'testWrite', '        &nbsp;<p style=\"&quot;text-indent:27.45pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><!--?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /--><st1:chsdate w:st=\"&quot;on&quot;\" isrocdate=\"&quot;False&quot;\" islunardate=\"&quot;False&quot;\" day=\"&quot;27&quot;\" month=\"&quot;3&quot;\" year=\"&quot;2014&quot;\"><span style=\"&quot;font-size:14pt;&quot;\" lang=\"&quot;EN-US&quot;\">3</span><span style=\"&quot;font-family:宋体;font-size:14pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">月</span><span style=\"&quot;font-size:14pt;&quot;\" lang=\"&quot;EN-US&quot;\">27</span><span style=\"&quot;font-family:宋体;font-size:14pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">日</span></st1:chsdate><span style=\"&quot;font-family:宋体;font-size:14pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">下午，根据训练部党委关于第二批党的群众路线教育实践活动的部署要求，图书馆组织开展了以</span><span style=\"&quot;font-family:宋体;font-size:15pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">“践行群众路线、营造书香氛围”为主题的送书下基层的活动。本次活动由图书馆与通信站联合主办，图书馆刘春林馆长、敬卿副馆长、通信站谭琳站长以及图书馆和通信站部分官兵参加了这一活动。</span><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--><o:p></o:p></span></p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-family:宋体;font-size:15pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">图书馆始终坚持“以人为本、读者第一”的服务宗旨，深入了解通信站官兵的阅读需求，选送图书</span><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\">500</span><span style=\"&quot;font-family:宋体;font-size:15pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">余册，期刊</span><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\">230</span><span style=\"&quot;font-family:宋体;font-size:15pt;mso-ascii-font-family:&#39;Times\" new=\"\" roman\';mso-hansi-font-family:\'times=\"\" roman\';\"=\"\">多册，受到通信站官兵的热烈欢迎。图书馆本次送书下基层活动，不仅极大地丰富了通信站官兵的业余文化生活，拓宽和拓展了图书的流通渠道和利用空间，也是结合党的群众路线教育，切实改进工作作风，确保进一步做好读者服务工作的一次重要教育实践活动。</span><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><o:p></o:p></span></p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><o:p>&nbsp;</o:p></span></p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\" align=\"&quot;left&quot;\"><img border=\"&quot;0&quot;\" alt=\"&quot;&quot;\" src=\"images/110229.JPG\"></p> <p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><o:p></o:p></span>&nbsp;</p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><o:p><img border=\"&quot;0&quot;\" alt=\"&quot;&quot;\" src=\"images/110244.JPG\"></o:p></span></p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-size:15pt;&quot;\" lang=\"&quot;EN-US&quot;\"><o:p></o:p></span>&nbsp;</p><p style=\"&quot;text-indent:29.4pt;margin:0cm\" 0cm=\"\" 0pt;mso-char-indent-count:1.96;\"=\"\" class=\"&quot;MsoNormal&quot;\"><span style=\"&quot;font-size:15pt;&quo