INSERT INTO `tk_project` VALUES ('2', '久联项目任务管理系统（JWSS）的建设与实施', 'J2005-0001', '<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">项目代码：J2005-0001</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">项目类型：公司内部项目</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">项目状态：非项目</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">项目目标：</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">&nbsp; &nbsp; &nbsp; &nbsp;建设项目任务管理系统，将公司的日常工作纳入项目任务管理系统中。同时，项目任务管理系统的运行数据是员工绩效考核的依据。<br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">项目内容：</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;1、在公司服务器上搭建<strong><span style=\"color:#E53333;\">”</span></strong><strong><span style=\"color:#E53333;\">久联项目任务管理系统</span></strong><strong><span style=\"color:#E53333;\"></span></strong><strong><span style=\"color:#E53333;\">”，</span></strong></span>采用WSS的开源代码进行适当修改。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2、将<strong><span style=\"color:#E53333;\">”</span></strong><strong><span style=\"color:#E53333;\">久联项目任务管理系统</span></strong><strong><span style=\"color:#E53333;\"></span></strong><strong><span style=\"color:#E53333;\">”</span></strong><span style=\"color:;\">与软件开发项目、工程项目、公司日常事务、维护任务等流程相结合，制定出一整套管理流程。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span>3、将</span><strong><span style=\"color:#E53333;\">”</span></strong><strong><span style=\"color:#E53333;\">久联项目任务管理系统</span></strong><strong><span style=\"color:#E53333;\"></span></strong><strong><span style=\"color:#E53