INSERT INTO `tk_task` VALUES ('1', '1', '2', '1', '2', '0', '0', '2', '0', '26', '公司服务器上搭建”久联项目任务管理系统”', '3', '3', '2014-12-28', '2014-12-29', '12.0', '<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#F3F3F3;\">公司服务器上搭建</span><strong><span style=\"color:#E53333;\">”</span></strong><strong><span style=\"color:#E53333;\">久联项目任务管理系统</span></strong><strong><span style=\"color:#E53333;\"></span></strong><strong><span style=\"color:#E53333;\">”。，使其运行正常。</span></strong>使公司员工都可以正常访问。\r\n</p>\r\n<p>\r\n	<strong><span>采用WSS的开源代码，<span style=\"color:#444444;font-family:sans-serif;font-size:13px;line-height:normal;background-color:#F3F3F3;\">&nbsp;MariaDB数据库。</span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span>服务端地址：http://192.168.1.254/wss/</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span>为感谢WSS项目的开源，我们把公司的任务管理系统简称为：<strong><span style=\"color:#E53333;\">JWSS</span></strong><span style=\"color:#E53333;\"></span></span></strong>\r\n</p>\r\n<p>\r\n	<strong><span></span></strong> \r\n</p>', '25', '2014-12-29 11:45:47', '-1', '>>-1', '-1', '', null, '', '任务管理 系统建设', '', '', '2', '2', '2014-12-29 11:46:29');
