INSERT INTO `tk_task` VALUES ('9', '1', '2', '1', '2', '0', '0', '4', '0', '19', '太原市政设计院售后服务', '3', '3', '2014-12-24', '2014-12-24', '4.0', '<p>\r\n	用户：太原市政设计院\r\n</p>\r\n<p>\r\n	接报：用户小牛申报，文件不能上传。\r\n</p>\r\n<p>\r\n	处理：电话指导或上门服务。\r\n</p>\r\n<p>\r\n	结果：\r\n</p>\r\n<p>\r\n	1、请张森电话指导，设置浏览器的安全模式，不能解决。\r\n</p>\r\n<p>\r\n	2、请设计院乔工协助实地解决，<span>设置浏览器的安全模式，不能解决。</span>\r\n</p>\r\n<p>\r\n	<span>3、经理上门解决，发现是用户停电3~4小时后，UPS供电时间过长，造成服务器停电，来电后，服务器自动启动，但公司开发的TServer是以程序运行的，在WINSERVER2008下，不人工登录一下服务器，<span>TServer不启动，造成的。</span><span>TServer不启动，触摸屏也读不到数据。</span></span>\r\n</p>\r\n<p>\r\n	<span>建议：</span>\r\n</p>\r\n<p>\r\n	<span>1、向用户提议，更换一批UPS电池。</span>\r\n</p>\r\n<p>\r\n	<span>2、改写TSERVER为服务，使系统不登录也可以启动。</span>\r\n</p>', '25', '2014-12-29 11:30:07', '-1', '>>-1', '-1', '', null, '', '设计院 售后服务', '', '', '2', '2', '2014-12-29 11:32:21');
