INSERT INTO `tk_task` VALUES ('31', '1', '2', '1', '5', '0', '0', '4', '0', '19', '离石区看守所维护2014-12-31', '3', '3', '2014-12-31', '2015-01-01', '0.0', '<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	用户：离石区看守所\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	接报：刘科长上报，大部分屏幕黑屏。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	处理：上门服务。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	预估：可能触摸一体机的硬盘有问题，造成了TCLIECT读取配置参数失败，写成了默认参数，不能联网。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	要求：\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;1、现场维护完成后，填写售后维护登记表，用户确认签字。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;2、回公司后，填写任务日志，维护表归档，<span style=\"color:#E53333;\">填写：</span><span style=\"color:#E53333;font-family:Arial;background-color:#FFFFFF;\"><strong>吕梁</strong></span><span style=\"color:#E53333;font-family:Arial;background-color:#FFFFFF;\"><strong>离石区看守所维护记录表</strong></span>。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	辅助要求：顺带查看交换机，理清4、5、6监区的网络结构，能否建立虚网。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	器材准备：笔记本、U盘，长网线1条、网线钳、水晶头、交换机调试线，打印好的维护登记表。USB键盘 鼠标\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	软件准备：离石区运行参数表，tclient tserver可运行程序，可运行主程序，<span style=\"color:#333333;font-family:Arial;background-color:#FFFFFF;\">tclient 源代码。</span> \r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	预估费用：1000元，主要包括过路费、油费，可能一天的住宿费。\r\n</p>\r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>', '25', '2014-12-31 18:37:16', '-1', '>>-1', '-1', '', '每次任务完成，记得在“售后服务汇总表”中登记', '', '离石区看守所', '', '', '2', '2', '2014-12-31 18:38:43');
