INSERT INTO `tk_task` VALUES ('320', '1', '8', '1', '8', '0', '0', '20', '0', '15', '第四次09-06部署总结报告', '3', '3', '2015-09-05', '2015-09-12', '0.0', '<p>\r\n	本次部署主要包括以下两大块：\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n（一）更新部署x86版本，部署概况：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;x86版本除了OPAC数据对接相关功能以外都基本完成，且本次部署的版本运行比以往的版本要流畅，得到了用户的肯定，但是前端细节还存在一些比较明显的问题需要优化处理。<br />\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;OPAC数据对接相关模块需要的主要数据样本已采集到，下一步可以完成相关功能模块的开发工作。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n（二）首次部署安卓触摸版APP，部署概况：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;安卓版首次部署过程还算顺利，终端设备上的APP程序运行稳定。遇到的比较严重的问题就是与Tserver网络较差通信（主要表现为长时间运行以及次日重启后掉线）。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外，由于APP界面比较简单，用户提出了一些在界面设计上以及内容丰富上的优化建议。<br />\r\n<br />\r\n<br />\r\n<p>\r\n	【附件一】\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;项目部署问题及优化建议总结文档：\\\\192.168.1.254\\软件项目区\\长沙国防科技大学图书馆\\第四次部署情况\\需求与改进建议_20150915.xlsx\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	【附件二】\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;供设计人员参考的素材（图片）：\\\\192.168.1.254\\软件项目区\\长沙国防科技大学图书馆\\第四次部署情况\\安卓广告机素材\r\n</p>', '25', '2015-09-15 18:01:35', '-1', '>>-1', '-1', '', null, '', '', '', '', '8', '8', '2015-09-16 08:37:18');
