INSERT INTO `tk_task` VALUES ('473', '1', '14', '1', '13', '0', '0', '16', '0', '3', '新建值日管理、考试管理、作业管理模块', '3', '3', '2016-04-26', '2016-04-27', '16.0', '<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	事项：\r\n</p>\r\n<ol style=\"color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<li>\r\n		<span style=\"line-height:1.5;\">新建值日管理模块，实现班主任教师对本班值日安排（以周为单位安排值日）；</span> \r\n	</li>\r\n	<li>\r\n		新建考试管理模块，实现管理员对考试安排、考场安排的增删改查；\r\n	</li>\r\n	<li>\r\n		新建作业管理模块，实现各班老师对各科作业的增删改查（新增作业时，同时为其新增本班所有学生的作业提交情况记录，新增的作业提交情况记录中的homeworkId为本作业的Id，studentId为各学生的Id），及作业的提交情况进行标识（通过isSubmit字段标识）、评论、评价（优、良、差）。<br />\r\n	</li>\r\n</ol>\r\n<span style=\"color:#333333;font-family:Arial;line-height:1.5;background-color:#FFFFFF;\">要求：</span><span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#FFFFFF;\"></span><span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#FFFFFF;\"></span> \r\n<p style=\"margin-left:5px;color:#333333;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp;编写管理模块的表都已经存在，在ER图里可以找到表名、字段名，如果有遗漏，告诉我，我来新增<span></span>，除此之外，还要对这些模块进行单元测试，测试数据要真实。</span> \r\n</p>', '14', '2016-09-02 16:10:01', '427', '427>2', '2', '', '', '', '', '', '', '14', '13', '2016-09-02 16:10:08');
