INSERT INTO `tk_task` VALUES ('641', '1', '14', '1', '13', '0', '0', '16', '0', '3', '学校设置调整', '3', '3', '2016-07-22', '2016-07-22', '0.0', '<ol>\r\n	<li>\r\n		学校名称长度限制为2~20个汉字，做数据验证\r\n	</li>\r\n	<li>\r\n		学校编码长度最长为15个字母，可不填，做数据验证\r\n	</li>\r\n	<li>\r\n		校训长度最长限制为2~20个汉字，做数据验证\r\n	</li>\r\n	<li>\r\n		介绍长度最长为500个汉字，可不填，做数据验证\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:04:34', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:04:41');
INSERT INTO `tk_task` VALUES ('642', '1', '14', '1', '13', '0', '0', '16', '0', '3', '投票管理调整', '3', '3', '2016-07-22', '2016-07-22', '0.0', '<ol>\r\n	<li>\r\n		增加投票名称\r\n	</li>\r\n	<li>\r\n		投票结果统计，调整显示顺序，先显示选项，后显示得票数，最后显示得票所占百分比\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:05:42', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:05:45');
INSERT INTO `tk_task` VALUES ('643', '1', '14', '1', '13', '0', '0', '16', '0', '3', '座位表管理', '3', '3', '2016-07-22', '2016-07-26', '0.0', '<ol>\r\n	<li>\r\n		如果某个用户的<span style=\"line-height:1.5;\">数据权限是多个班级，点击“座位表”栏目显示班级列表，可以按班级筛选。每行上有一个“座位表”按钮，点击该按钮，可以在新页面编辑座位表。如果该用户的数据权限只有一个班级，则直接显示此班级的座位表</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;\">\r\n		<p>\r\n			根据班级对应的教室的行数和列数，排列一个具有相同行数和列数矩阵，类似于课程表的样子，然后可以为每个单元格选择该班的学生<span style=\"line-height:1.5;\"></span>\r\n		</p>\r\n</span>\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:00:43', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:00:47');
