INSERT INTO `tk_task` VALUES ('648', '1', '14', '1', '13', '0', '0', '16', '0', '3', '通知管理调整', '3', '3', '2016-07-27', '2016-07-27', '0.0', '<ol>\r\n	<li>\r\n		去掉通知分类\r\n	</li>\r\n	<li>\r\n		去掉开始时间，保留结束时间\r\n	</li>\r\n	<li>\r\n		如果不选择班级，意味此通知为全校通知，则保存时通知对应的班级字段为空，以表示此通知为全校通知\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:03:08', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:03:15');
INSERT INTO `tk_task` VALUES ('649', '1', '14', '1', '13', '0', '0', '16', '0', '3', '值日管理调整', '3', '3', '2016-07-28', '2016-07-28', '0.0', '<ol>\r\n	<li>\r\n		如果<span style=\"line-height:1.5;\">某个用户的数据权限是多个班级，点击“值日管理”栏目显示班级列表，可以按班级筛选。每行上有一个“值日管理”按钮，点击该按钮，可以在新页面编辑值日表。如果该用户的数据权限只有一个班级，则直接显示此班级的值日表</span>\r\n	</li>\r\n	<li>\r\n		值日表<span style=\"line-height:1.5;\">可以是一个简单的列表，列表的左侧是周一、周二、周三、周四、周五、周六、周日，右侧是用逗号分隔的学生序列，在弹出框里进行选择</span>\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:01:12', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:01:21');
INSERT INTO `tk_task` VALUES ('650', '1', '14', '1', '13', '0', '0', '16', '0', '3', '终端管理调整', '3', '3', '2016-07-29', '2016-07-29', '0.0', '<ol>\r\n	<li>\r\n		将<span style=\"line-height:1.5;\">“班牌管理”改回“终端管理”</span>\r\n	</li>\r\n	<li>\r\n		去掉<span style=\"line-height:1.5;\">教室、监测设备、班牌类型、</span><span style=\"line-height:1.5;\">MAC</span><span style=\"line-height:1.5;\">、读卡器</span>\r\n	</li>\r\n	<li>\r\n		增加<span style=\"line-height:1.5;\">IP</span><span style=\"line-height:1.5;\">摄像头，只用于显示</span>\r\n	</li>\r\n</ol>', '14', '2016-08-05 18:01:34', '632', '427>2>632>3', '3', '', '', '', '', '', '', '14', '13', '2016-08-05 18:01:43');
