INSERT INTO `tk_task` VALUES ('845', '1', '2', '1', '11', '0', '0', '56', '0', '34', '投标报名事务', '3', '3', '2016-09-18', '2016-09-19', '0.0', '<p>\r\n	1、报名要求沟通\r\n</p>\r\n<p>\r\n	2、报名资料的准备\r\n</p>\r\n<p>\r\n	【\r\n</p>\r\n<p>\r\n	1．营业执照副本、税务登记证（国税或地税）副本、组织机构代码证副本（或新版“三证合一”营业执照）；<br />\r\n2．如报价人代表是法定代表人，提供法定代表人的身份证明书及法定代表人身份证原件；<br />\r\n3．如报价人代表不是法定代表人，经办人需持有《法定代表人授权书》（原件）及法人身份证复印件、经办人身份证；<br />\r\n4．截至提交响应文件时间前一年内任意一次缴纳社会保障资金的凭证；<br />\r\n5．截至提交响应文件时间前半年内任意一个月税收凭证；<br />\r\n6．报价人须提供具备审计资格的第三方出具的2015年度审计报告；<br />\r\n7. 所投硬件产品具有的中国国家强制性产品认证证书3C；<br />\r\n8. 所投硬件产品生产厂家需要有第三方检测机构认证的全国质量检测稳定合格产品证书；<br />\r\n9. 所投软件产品具有的中华人民共和国国家版权局出具的信息展示系统计算机软件著作权登记证书；<br />\r\n（以上证书均须携带原件供查验，同时提供加盖单位公章的复印件1套备案）\r\n</p>\r\n<p>\r\n	】\r\n</p>\r\n<p>\r\n	准备资料，报名。\r\n</p>\r\n<p>\r\n	1、华远、中视的资料\r\n</p>\r\n<p>\r\n	2、英伟特资料\r\n</p>\r\n<p>\r\n	3、授权英伟特的产品资料\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '25', '2016-09-22 08:50:32', '844', '844>2', '2', '', null, '', '', '', '', '2', '2', '2016-09-22 11:59:25');
