INSERT INTO `tk_task` VALUES ('910', '1', '2', '1', '2', '0', '0', '19', '0', '12', '2016-10-08公司会议', '3', '3', '2016-10-08', '2016-10-09', '1.5', '<p>\r\n	一、交流国庆的活动情况\r\n</p>\r\n<p>\r\n	二、交流公司与员工的关系\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1、公司的使命是创造财富\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 2、公司最重要的资源是人，公司的目标是将自由人组成有机组织，创造出顾客需要的，愿意付钱的产品和服务。公司不是员工的家，是员工工作、创造绩效的地方。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 3、组织的目的就是<span>让平凡的人做出不平凡的事。</span> \r\n</p>\r\n<p>\r\n	三、交流公司的奖励原则和绩效考核等思路\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 1、公司的四种人:\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;不适合的劳动者：<span>在工作过程中总是喜欢找借口，拖延、倦怠已经成为来他们的习惯，这类人最终会被企业淘汰的。</span>\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;普通劳动者：<span><span>做事勤恳，能够尽量把任务完成，但容易满足于现状而失去进取心，最后也只能沦落为平庸的员工。</span></span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;个人奋斗者：<span>做事勤恳，能够尽量把任务完成，也很努力地奋斗，但奋斗目标与公司的目标不一致。</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;真正的奋斗者：他们尽职尽责，精益求精，拥有超强的执行力，做事永远超出公司的期待。除此之外，他们做事高效，还善于思考，凡事力求做到最好。<span>奋斗目标与公司的目标一致，知道团体的力量远大于个人的力量，愿意与公司一起发展。</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 2、奖励为公司奋斗取得绩效的人\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 3、细化公司的岗位和绩效标准\r\n</p>\r\n<p>\r\n	四、10月工作按排\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1、信发系统安卓广告版的开发进度（后台、安卓）\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 2、信发系统数字班牌版的开发进度（X86、安卓）\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 3、省人大项目安排\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 4、中阳煤矿项目实施\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 5、孝义看守所投标\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; 6、其它项目：招标公司的排队机、许永坤的供货回款\r\n</p>', '5', '0000-00-00 00:00:00', '-1', '>>-1', '-1', '', '', '', '', '', '', '2', '2', '2016-10-08 15:34:59');
