INSERT INTO `tk_task_byday` VALUES ('49', '20150113', '14', '0.0', '<p>\r\n	处理情况：\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;①原因：公司网站服务器IP调整及PHP版本升级后，后台登录成功出现空白页。\r\n</p>\r\n<p>\r\n	<span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;②问题分析：<span style=\"color:#333333;\">include/userlogin.class.php，里面有一个keepuser()函数，是用session_register来注册一个session变量，但是在版本升级后php5.4中已经移除了这个功能。</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;③解决方法：把@session_register($this-&gt;keepUserIDTag);&nbsp;&nbsp;&nbsp; 注释掉，然后改为<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!isset($_SESSION[$this-&gt;keepUserIDTag]))</span>\r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span></span>如下：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!isset($_SESSION[$this-&gt;keepUserIDTag]))<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a>//@session_register($this-&gt;keepUserIDTag</a>);\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n	<table style=\"background-color:#ffbb77;margin:20px 0px;color:#333333;\" border=\"0\" cellspacing=\"1\" cellpadding=\"1\" width=\"620\" align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td style=\"color:#000000;\" bgcolor=\"#ffe7ce\" height=\"27\" width=\"464\">\r\n					代码如下\r\n				</td>\r\n				<td style=\"color:#000000;\" bgcolor=\"#ffe7ce\" width=\"109\" align=\"middle\">\r\n					&nbsp;\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style=\"background-color:#fff4e8;color:#000000;font-size:14px;\" bgcolor=\"#ffffff\" height=\"auto\" valign=\"top\" colspan=\"2\">\r\n					<p>\r\n						@session_register($this-&gt;keepUserIDTag);<br />\r\n$_SESSION[$this-&gt;keepUserIDTag] = $this-&gt;userID;<br />\r\n@session_register($this-&gt;keepUserTypeTag);<br />\r\n$_SESSION[$this-&gt;keepUserTypeTag] = $this-&gt;userType;<br />\r\n@session_register($this-&gt;keepUserChannelTag);<br />\r\n$_SESSION[$this-&gt;keepUserChannelTag] = $this-&gt;userChannel;<br />\r\n@session_register($this-&gt;keepUserNameTag);<br />\r\n$_SESSION[$this-&gt;keepUserNameTag] = $this-&gt;userName;<br />\r\n@session_register($this-&gt;keepUserPurviewTag);<br />\r\n$_SESSION[$this-&gt;keepUserPurviewTag] = $this-&gt;userPurview;<br />\r\n@session_register($this-&gt;keepAdminStyleTag);<br />\r\n$_SESSION[$this-&gt;keepAdminStyleTag] = $adminstyle;<br />\r\nPutCookie(‘DedeUserID’, $this-&gt;userID, 3600 * 24, ‘/’);<br />\r\nPutCookie(‘DedeLoginTime’, time(), 3600 * 24, ‘/’);\r\n					</p>\r\n					<p>\r\n						替换成\r\n					</p>\r\n					<p>\r\n						global $admincachefile,$adminstyle;<br />\r\nif(empty($admin<span><a href=\"http://cpro.baidu.com/cpro/ui/uijs.php?rs=1&amp;u=http%3A%2F%2Fwww%2E3lian%2Ecom%2Fedu%2F2013%2F12%2D26%2F118872%2Ehtml&amp;p=baidu&amp;c=news&amp;n=10&amp;t=tpclicked3_hc&amp;q=3liancpr&amp;k=style&amp;k0=style&amp;kdi0=8&amp;sid=da4a777f163bde5d&amp;ch=0&amp;tu=u1833515&amp;jk=b6efbd665a1eaa95&amp;cf=29&amp;fv=11&amp;stid=9&amp;urlid=0&amp;luki=1&amp;seller_id=1&amp;di=8\" target=\"_blank\"><span style=\"color:#0000ff;font-size:14px;\">style</span></a></span>)) $adminstyle = ‘dedecms’;<br />\r\n//@session_register($this-&gt;keepUserIDTag);<br />\r\n$_SESSION[$this-&gt;keepUserIDTag] = $this-&gt;keepUserIDTag;<br />\r\n$_SESSION[$this-&gt;keepUserIDTag] = $this-&gt;userID;<br />\r\n//@session_register($this-&gt;keepUserTypeTag);<br />\r\n$_SESSION[$this-&gt;keepUserTypeTag] = $this-&gt;keepUserTypeTag;<br />\r\n$_SESSION[$this-&gt;keepUserTypeTag] = $this-&gt;userType;<br />\r\n// @session_register($this-&gt;keepUserChannelTag);<br />\r\n$_SESSION[$this-&gt;keepUserChannelTag] = $this-&gt;keepUserChannelTag;<br />\r\n$_SESSION[$this-&gt;keepUserChannelTag] = $this-&gt;userChannel;<br />\r\n//@session_register($this-&gt;keepUserNameTag);<br />\r\n$_SESSION[$this-&gt;keepUserNameTag] = $this-&gt;keepUserNameTag;<br />\r\n$_SESSION[$this-&gt;keepUserNameTag] = $this-&gt;userName;<br />\r\n//@session_register($this-&gt;keepUserPurviewTag);<br />\r\n$_SESSION[$this-&gt;keepUserPurviewTag] = $this-&gt;keepUserPurviewTag;<br />\r\n$_SESSION[$this-&gt;keepUserPurviewTag] = $this-&gt;userPurview;<br />\r\n// @session_register($this-&gt;keepAdminStyleTag);<br />\r\n$_SESSION[$this-&gt;keepAdminStyleTag] = $this-&gt;keepAdminStyleTag;<br />\r\n$_SESSION[$this-&gt;keepAdminStyleTag] = $admin<span><a href=\"http://cpro.baidu.com/cpro/ui/uijs.php?rs=1&amp;u=http%3A%2F%2Fwww%2E3lian%2Ecom%2Fedu%2F2013%2F12%2D26%2F118872%2Ehtml&amp;p=baidu&amp;c=news&amp;n=10&amp;t=tpclicked3_hc&amp;q=3liancpr&amp;k=style&amp;k0=style&amp;kdi0=8&amp;sid=da4a777f163bde5d&amp;ch=0&amp;tu=u1833515&amp;jk=b6efbd665a1eaa95&amp;cf=29&amp;fv=11&amp;stid=9&amp;urlid=0&amp;luki=1&amp;seller_id=1&amp;di=8\" target=\"_blank\"><span style=\"color:#0000ff;font-size:14px;\">style</span></a></span>;\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</p>', '0', '2015-01-13 17:46:12', '53', '5', '10', '7');
