INSERT INTO `tk_task_byday` VALUES ('402', '20150529', '9', '0.0', '<p>\r\n	处理右边无触摸机器和中间有水印机器。两台高拍仪未到。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"background-color:#009900;font-size:16px;\"><strong>安装遇到的问题及解决方法</strong></span>\r\n</p>\r\n<p>\r\n	&nbsp;1.机器中间有水印。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 先在机器后垫点泡沫，不行则打开机器，在触摸屏和液晶屏之间在粘一圈红胶带，用布将屏擦干净。\r\n</p>\r\n<p>\r\n	&nbsp;2.机器右边无触摸。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 先更新触摸驱动（侧边点击问题解决.bin），更新后若还不能触摸，则使用千里触控里面的<span style=\"color:#ff9900;\">Diagnostic Tools.exe</span>，检测下触摸屏是否有坏点。如果有问题则更换红外管。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', '0', '2015-05-30 08:53:41', '250', '10', '21', '15');
