INSERT INTO `tk_task_byday` VALUES ('485', '20150907', '14', '4.0', '<p>\r\n	1.<span style=\"color:black;font-family:宋体;font-size:14px;\">教学楼一楼管理</span><span style=\"color:black;font-family:Calibri;font-size:14px;\">PC无法登录发布节目，经检查为PC的IP地址改为自动获取和广告机不在一个网段，根据维护资料恢复IP设置为<span style=\"color:#4C33E5;font-size:18px;\">192.168.100.1/24</span>后正常登录。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:black;font-family:Calibri;font-size:14px;\"><span style=\"font-size:14px;\">2</span><span style=\"font-size:14px;\">.</span></span><span style=\"color:black;font-family:Calibri;\"><span style=\"color:black;font-family:宋体;font-size:14px;\">通过</span><span style=\"color:black;font-family:Calibri;font-size:14px;\">Netclient.exe</span><span style=\"color:black;font-family:宋体;font-size:14px;\">客户端登录到服务器</span></span><span style=\"color:black;font-family:Calibri;font-size:14px;\"><span style=\"font-size:14px;\">登</span></span><span style=\"color:black;font-family:Calibri;font-size:14px;\">录后，3号教学楼全部离线，经测试为3号教学楼7楼竖井交换机断电所致，多次插拔电源线后恢复正常。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:black;font-family:Calibri;font-size:14px;\">3.全部开启后，客户设置定时开关机，个别广告机开机一会后自动关机，过一会又开机。不知是软件设置有问题还是何故。暂时正常。</span>\r\n</p>', '0', '2015-09-08 08:57:17', '311', '10', '4', '19');
