INSERT INTO `tk_task_byday` VALUES ('822', '20160531', '9', '4.0', '<p>\r\n	从大屏处把视频线布到接线台、指挥席、控制台\r\n</p>\r\n<p>\r\n	接线台4根dvi和1根VGA线\r\n</p>\r\n<p>\r\n	指挥台2根dvi和1根VGA\r\n</p>\r\n<p>\r\n	控制台7根dvi和1根VGA\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	准备在控制台及指挥台上各布1根话筒线，准备今天联系送货，进场布线。\r\n</p>\r\n<p>\r\n	施工现场图\\\\192.168.1.254\\工程项目区\\集成类工程\\太原市食药监局办公楼举报中心\\施工现场\\2015年5月30日\r\n</p>', '0', '2016-06-01 09:00:08', '484', '15', '37', '20');
