INSERT INTO `tk_task_byday` VALUES ('1204', '20160902', '9', '6.0', '<p>\r\n	<span style=\"color:#333333;font-family:Arial;line-height:20px;background-color:#FFFFFF;\">.华远询问厂家，先进入工厂模式，选择老化模式，开启三分钟，在设置通电自动亮。测试下是否能长期生效。</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:20px;background-color:#FFFFFF;\">在5楼试了老化模式，并不能解决问题，怀疑是晚上电脑关机时间与时控开关断电时间相差太长。把时控开关断电时间与电脑关机时间相差两分钟，测试了亮天，正常。</span></span>\r\n</p>', '0', '2016-09-05 09:04:14', '750', '10', '4', '19');
