INSERT INTO `tk_task_byday` VALUES ('1256', '20160918', '5', '0.0', '<p>\r\n	招标事项\r\n</p>\r\n<p>\r\n	1、投标报名\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 1） 拿到华远、中视同创公章（<span>公章发顺风快递</span>）；\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"line-height:1.5;\">王总身份证、</span><span style=\"line-height:1.5;\">中视同创的</span><span style=\"line-height:1.5;\">3C、</span><span style=\"line-height:1.5;\">全国质量检测稳定合格产品证书、信息展示系统计算机软件著作权登记证书 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span></span>联系丁方 &nbsp;18510944926&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 2）做英伟特需要P的证书（<span style=\"line-height:1.5;\">3C、</span><span style=\"line-height:1.5;\">全国质量检测稳定合格产品证书、信息展示系统计算机软件著作权登记证书 &nbsp;&nbsp;</span>） &nbsp; &nbsp; 联系人：<span>崔秀梅</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;英伟特陪标产品用谁家？&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 联系人：王总\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 3）授权人：华远--王前 &nbsp; &nbsp;中视同创--荆盘 &nbsp; &nbsp; 英伟特---罗爱伟\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;报名费用：300元一份。共900元\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;报名时间：9月20号\r\n</p>\r\n<p>\r\n	2、准备标书\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 1）正本一份；副本二份\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 2）废标项：\r\n</p>\r\n<p>\r\n	商务部分：\r\n</p>\r\n<p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		A、报价函；\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		B、<span>*</span>法定代表人身份证明书<span> </span>；<span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		C、<span>*</span>法定代表人授权委托书；<span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		D、<span>*</span>有效的营业执照副本、有效的税务登记证（国税、地税）副本、有效的组织机构代码证副本（或新版“三证合一”营业执照）；\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		E、<span>*</span>报价截止日前一年任意一次社保金交纳凭证；\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		F、<span>*</span>报价截止日前半年任意一项社税收交纳凭证；\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		G、<span>*</span>具备审计资格的第三方出具的2015年度审计报告；&nbsp;&nbsp;\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:26pt;\">\r\n		H、<span>*</span>所投硬件产品具有的中国国家强制性产品认证证书<span>3C</span>；<span></span>\r\n	</p>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;I、<span>*</span>所投硬件产品生产厂家需要有第三方检测机构认证的全国质量检测稳定合格产品证书；\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"line-height:1.5;\">J、</span><span style=\"line-height:1.5;\">*</span><span style=\"line-height:1.5;\">所投软件产品具有的中华人民共和国国家版权局出具的信息展示系统计算机软件著作权登记证书；</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"color:#E53333;\">&nbsp;高新企业证书及IS9001证书待定</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\"><span style=\"color:#000000;\">技术部分：</span>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">A、</span><span style=\"color:#000000;\">*</span><span style=\"color:#000000;\">报价一览表</span><span style=\"color:#000000;\"> (</span><span style=\"color:#000000;\">见‘报价文件格式’</span><span style=\"color:#000000;\">)</span><span style=\"color:#000000;\">；</span><span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">B、</span><span style=\"color:#000000;\">*</span><span style=\"color:#000000;\">报价货物主要技术指标和运行性能的详细描述；</span><span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">C、</span><span style=\"color:#000000;\">*</span><span style=\"color:#000000;\">报价货物技术规范偏离表</span><span style=\"color:#000000;\">(</span><span style=\"color:#000000;\">见‘报价文件格式’</span><span style=\"color:#000000;\">)</span><span style=\"color:#000000;\">；</span><span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">D、</span><span style=\"color:#000000;\">*</span><span style=\"color:#000000;\">商务条款响应表</span><span style=\"color:#000000;\">(</span><span style=\"color:#000000;\">见‘报价文件格式’</span><span style=\"color:#000000;\">)</span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">E、</span><span style=\"color:#000000;\">*</span><span style=\"color:#000000;\">售后服务响应、保修承诺书</span><span style=\"color:#000000;\">(</span><span style=\"color:#000000;\">见‘报价文件格式’</span><span style=\"color:#000000;\">)</span><span style=\"color:#000000;\">；</span><span></span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">3）标书规格：双面打印、正文使用宋体小四号字体、表格内容使用宋体小五号字体、统一编目、编页码装订、投标文件严格按招标文件格式书写；</span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">4）投标保证金：金额：4000元</span>\r\n	</p>\r\n	<p align=\"left\" style=\"text-indent:19.5pt;\">\r\n		<span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp;\r\n		<p style=\"text-indent:54.2pt;\">\r\n			<b>收款单位：山西种能科技有限公司</b><b></b>\r\n		</p>\r\n		<p style=\"text-indent:54.2pt;\">\r\n			<b>开户银行：建设银行南内环街支行</b><b></b>\r\n		</p>\r\n		<p style=\"text-indent:54.2pt;\">\r\n			<b>帐</b><b>&nbsp;&nbsp;&nbsp; </b><b>号：</b><b>1400 1825 9080 5050 1709&nbsp;</b>\r\n		</p>\r\n&nbsp; &nbsp; &nbsp; &nbsp; 北京华远、中视同创 &nbsp; &nbsp;&nbsp;联系人：丁方 &nbsp; &nbsp;9月20日打款</span>\r\n	</p>\r\n&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#000000;\">山西英伟特 &nbsp; 联系人：冯燕</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\">&nbsp; &nbsp; &nbsp; &nbsp; 5）投标文件密封：封皮上写明项目编号、项目名称、报价人全称、地址，并注明“谈判时启封”字样。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">三、投标开始</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\"><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; 三轮谈判：1、</span><b><span style=\"color:#000000;\">技术谈判 &nbsp; &nbsp;确定供应商是否满足招标要求</span></b></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\"><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2、公开报价 &nbsp; &nbsp;&nbsp;</span><span style=\"color:#000000;\">调整性报价，报价采用书面方式</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3、商务谈判 &nbsp; &nbsp; 修改商务条款、做出售后承诺</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\"><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4、最终报价 &nbsp; &nbsp; </span><span style=\"color:#000000;\"></span><span style=\"color:#000000;\"></span><span style=\"color:#000000;\"></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;\"><span style=\"color:#000000;\"><br />\r\n</span></span>\r\n</p>', '0', '2016-09-18 09:50:25', '844', '11', '56', '34');
